// Helper function to calculate time difference in minutes and return formatted offset
function calculateOffset(time1, time2) {
    if (time1 === "N/A" || time2 === "N/A") return "N/A";

    // Check if both times are valid dates
    if (!isValidDate(time1) || !isValidDate(time2)) return "Invalid Date";

    let t1 = new Date(time1);
    let t2 = new Date(time2);

    let diffMs = t1.getTime() - t2.getTime(); // Difference in milliseconds
