    // return diffMins >= 0 ? `+${diffMins} min` : `${diffMins} min`;
    // Reverse the sign of the time difference
    let reversedDiffMins = diffMins * -1;

    return reversedDiffMins >= 0 ? `+${reversedDiffMins} min` : `${reversedDiffMins} min`;
}

